                    new BookEntityModel { Title = "The Fault In Our Stars", Author = "John Green", ISBN10 = "0525426000", ISBN13 = "9780525426004", Description = "'The greatest romance story of this decade.' —Entertainment Weekly", NumberOfPages = 324, DatePublished = new DateModel(2013, 01, 23), Publisher = "Penguin Putnam Inc", Rating = 4.5, NumberOfRatings = 122, NumberOfCopiesSold = 220, InStock = 170, Genre="", Price = 9.99},
